<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Fast_link_model extends Base_lang_image_model {	function __construct()    {        // Call the Model constructor        parent::__construct();		$this->table_name = "fast_link";		$this->primary_key = "id_fast_link";		$this->unique_key = "url";		$this->image_column = "icon";		$this->lang_table_name = "fast_link_lang";		$this->lang_name = "text";		$this->lang_content = null;    }		function get_post_data()	{		$data = array();		if($_POST['online'] == null) {$_POST['online'] = 0;}		$data['insert'] = array(			'url' => $_POST['url'] ,			'order' => $_POST['order'] ,			'online' => $_POST['online'],			'icon' => $_POST['icon'] 		);		$data['insert_id'] = array(		   'lang' => 'id' ,		   'text' => $_POST['name_id']		);		if($_POST['name_en'] != null)		{			$data['insert_en'] = array(			   'lang' => 'en' ,			   'text' => $_POST['name_en']			);		}		else $insert_en = null;		return $data;	}		function add()	{		$data = $this->get_post_data();		return parent::add($data['insert'], $data['insert_id'], $data['insert_en']);	}		function edit()	{		$data = $this->get_post_data();		return parent::edit($data['insert'], $data['insert_id'], $data['insert_en']);	}}/* End of Fast Link Model *//* 26 Juli 2012 [13:16 PM] */?>