<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Highlight_model extends Base_lang_model {	function __construct()    {        // Call the Model constructor        parent::__construct();		$this->table_name 		= "highlight";		$this->primary_key 		= "id_highlight";		$this->unique_key 		= "name";		$this->lang_table_name	= "highlight_lang";		$this->lang_name		= "text";    }		function get_post_data()	{		$data = array();		if($_POST['online'] == null) {$_POST['online'] = 0;}		$data['insert'] = array(		   'name' => $_POST['name'],		   'order' => $_POST['order'],		   'online' => $_POST['online']		);		$data['insert_id'] = array(		   'lang' => 'id' ,		   'text' => $_POST['name_id']		);		if($_POST['name_en'] != null)		{			$data['insert_en'] = array(			   'lang' => 'en' ,			   'text' => $_POST['name_en']			);		}		else $insert_en = null;		return $data;	}		function add()	{		$data = $this->get_post_data();		return parent::add($data['insert'], $data['insert_id'], $data['insert_en']);	}		function edit()	{		$data = $this->get_post_data();		return parent::edit($data['insert'], $data['insert_id'], $data['insert_en']);	}}/* End of Highlight Model *//* 26 Juli 2012 [12:13 PM] */?>