<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Sukolilo_notes_model extends Base_lang_image_model {	function __construct()    {        // Call the Model constructor        parent::__construct();		$this->table_name 		= "sukolilo_notes";		$this->primary_key 		= "id_sukolilo_notes";		$this->unique_key 		= "name";		$this->lang_table_name	= "sukolilo_notes_lang";		$this->lang_name		= "title";		$this->lang_content		= "content";		$this->image_column		= "photo";    }		function get_post_data()	{		$data = array();		if($_POST['online'] == null) {$_POST['online'] = 0;}		$date_start = strtotime($_POST['date_start']);		$date_end = strtotime($_POST['date_end']);		if($date_end < $date_start){			$date_end = $date_start + TWO_WEEKS;		}		$date_end = $date_end + ONE_DAY - 1;		$data['insert'] = array(			'name' => $_POST['name'] ,			'link' => $_POST['link'] ,			'photo' => $_POST['photo'] ,			'start_date' => $date_start ,			'end_date' => $date_end ,			'online' => $_POST['online']		);		$data['insert_id'] = array(		   'lang' => 'id' ,		   'title' => $_POST['title_id'],		   'content' => $_POST['content_id']		);		if($_POST['title_en'] != null)		{			$data['insert_en'] = array(			   'lang' => 'en' ,			   'title' => $_POST['title_en'],			   'content' => $_POST['content_en']			);		}		else $insert_en = null;		return $data;	}		function add()	{		$data = $this->get_post_data();		return parent::add($data['insert'], $data['insert_id'], $data['insert_en']);	}		function edit()	{		$data = $this->get_post_data();		return parent::edit($data['insert'], $data['insert_id'], $data['insert_en']);	}}/* End of Sukolilo Notes Model *//* 26 Juli 2012 [13:21 PM] */?>